const print=document.querySelector(".print"),cancel=document.querySelector(".cancel"),back=document.querySelector(".back"),result=document.querySelector(".result"),numBtns=document.querySelectorAll(".btn"),division=document.querySelector(".division"),multiply=document.querySelector(".multiply"),plus=document.querySelector(".plus"),minus=document.querySelector(".minus"),numbers=Array.from(numBtns),btns=[division,multiply,plus,minus].concat(numbers);for(let e=0;e<btns.length;e++)btns[e].addEventListener("click",(()=>{print.innerText+=btns[e].innerHTML}));result.addEventListener("click",(()=>{try{print.innerText=eval(print.innerText)}catch(e){window.alert("계산이 안됩니다. \n"+e),print.innerText=""}})),cancel.addEventListener("click",(()=>{print.innerText=""})),back.addEventListener("click",(()=>{print.innerText=print.innerText.slice(0,-1)}));